mapper.select:
SELECT hm.title, m.profile_image_path, m.nickname, hm.createdDate, hm.imagePath, hm.content, COUNT(hml.likeId) AS likeCount # hmc.content
FROM healing_message hm JOIN member m ON hm.userNumber = m.userNumber # JOIN # # member m # ON # hm.userNumber = m.userNumber LEFT JOIN healing_message_like hml ON hm.messageId = hml.messageId WHERE hm.messageId = #{messageId} GROUP BY hm.messageId, m.profile_image_path, m.nickname, hm.createdDate, hm.imagePath, hm.content
# JOIN # healing_message_like hml # ON # hm.messageId = hml.messageId # WHERE # hm.messageId=#{messageId}

